              <CardContent className="p-4 space-y-2">
                <div className="flex justify-between text-sm">
                  <span>Sous-total:</span>
                  <span data-testid="text-subtotal">{cartState.total.toLocaleString()} DA</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span>Livraison:</span>
                  <span data-testid="text-delivery-cost">{deliveryPrice.toLocaleString()} DA</span>
                </div>
                <hr className="my-2" />
                <div className="flex justify-between text-lg font-bold text-primary">
                  <span>Total:</span>
                  <span data-testid="text-total-cost">{total.toLocaleString()} DA</span>
                </div>
              </CardContent>
            </Card>

            <Button
              type="submit"
              className="w-full bg-primary hover:bg-primary-dark"
              disabled={createOrderMutation.isPending || cartState.items.length === 0}
              data-testid="button-confirm-order"
            >
              <CreditCard className="h-4 w-4 mr-2" />
              {createOrderMutation.isPending ? "Traitement..." : "Confirmer la commande"}
            </Button>
          </form>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/product-card.tsx.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ShoppingCart, Heart, Check } from "lucide-react";
import { Product } from "@shared/schema";
import { Link } from "wouter";

interface ProductCardProps {
  product: Product;
  onAddToCart: (product: Product) => void;
}

export default function ProductCard({ product, onAddToCart }: ProductCardProps) {
  const [isAdded, setIsAdded] = useState(false);
  const [isImageLoaded, setIsImageLoaded] = useState(false);

  const handleAddToCart = () => {
    onAddToCart(product);
    setIsAdded(true);
    setTimeout(() => setIsAdded(false), 2000);
  };

  const formatPrice = (price: string) => {
    const numPrice = parseFloat(price);
    return `${numPrice.toLocaleString()} DA`;
  };

  const isOutOfStock = product.stock !== null && product.stock <= 0;

  return (
    <Link href={`/product/${product.id}`}>
      <Card 
        className="bg-white rounded-xl shadow-md hover:shadow-xl transition-shadow overflow-hidden group cursor-pointer"
        data-testid={`card-product-${product.id}`}
      >
      <div className="relative overflow-hidden">
        <div className={`w-full h-64 bg-gray-200 ${!isImageLoaded ? 'animate-pulse' : ''}`}>
          <img
            src={product.imageUrl}
            alt={product.name}
            className={`w-full h-64 object-cover group-hover:scale-105 transition-transform duration-300 ${
              isImageLoaded ? 'opacity-100' : 'opacity-0'
            }`}
            onLoad={() => setIsImageLoaded(true)}
            onError={() => setIsImageLoaded(true)}
            data-testid={`img-product-${product.id}`}
          />
        </div>

        {product.category === "nouveau" && (
          <Badge className="absolute top-4 left-4 bg-green-500 text-white">
            Nouveau
          </Badge>
        )}

        {isOutOfStock && (
          <Badge className="absolute top-4 left-4 bg-red-500 text-white">
            Rupture de stock
          </Badge>
        )}

      </div>

      <CardContent className="p-6">
        <h3 
          className="font-semibold text-lg text-gray-900 mb-2"
          data-testid={`text-product-name-${product.id}`}
        >
          {product.name}
        </h3>
        <p 
          className="text-gray-600 text-sm mb-4"
          data-testid={`text-product-description-${product.id}`}
        >
          {product.description || "Description non disponible"}
        </p>

        <div className="flex items-center justify-between">
          <div 
            className="text-2xl font-bold text-primary"
            data-testid={`text-product-price-${product.id}`}
          >
            {formatPrice(product.price)}
          </div>

          <Button
            onClick={handleAddToCart}
            disabled={isOutOfStock}
            className={`transition-colors ${
              isAdded 
                ? "bg-green-500 hover:bg-green-600" 
                : "bg-primary hover:bg-primary-dark"
            } ${isOutOfStock ? "bg-gray-400 cursor-not-allowed" : ""}`}
            data-testid={`button-add-cart-${product.id}`}
          >
            {isAdded ? (
              <>
                <Check className="h-4 w-4 mr-2" />
                Ajouté
              </>
            ) : (
              <>
                <ShoppingCart className="h-4 w-4 mr-2" />
                {isOutOfStock ? "Indisponible" : "Ajouter"}
              </>
            )}
          </Button>
        </div>

        {product.stock !== null && product.stock > 0 && product.stock <= 5 && (
          <p className="text-orange-500 text-xs mt-2" data-testid={`text-stock-warning-${product.id}`}>
            Plus que {product.stock} en stock !
          </p>
        )}
      </CardContent>
    </Card>
    </Link>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

