import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Tags, Trash2, Plus } from "lucide-react";
import { useState } from "react";

export default function CategoryManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [newCategoryName, setNewCategoryName] = useState("");

  const { data: categories = [], isLoading } = useQuery<string[]>({
    queryKey: ["/api/admin/categories"],
  });

  const createCategoryMutation = useMutation({
    mutationFn: async (name: string) => {
      const response = await apiRequest("POST", "/api/admin/categories", { name });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Catégorie créée",
        description: "La nouvelle catégorie a été créée avec succès",
      });
      setNewCategoryName("");
      queryClient.invalidateQueries({ queryKey: ["/api/admin/categories"] });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
    },
    onError: (error: any) => {
      toast({
        title: "Erreur",
        description: error.message === "Category already exists" ? "Cette catégorie existe déjà" : "Impossible de créer la catégorie",
        variant: "destructive",
      });
    },
  });

  const deleteCategoryMutation = useMutation({
    mutationFn: async (category: string) => {
      const response = await apiRequest("DELETE", `/api/admin/categories/${encodeURIComponent(category)}`, {});
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Catégorie supprimée",
        description: "La catégorie a été supprimée avec succès",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/categories"] });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
    },
    onError: (error) => {
      toast({
        title: "Erreur",
        description: "Impossible de supprimer la catégorie",
        variant: "destructive",
      });
    },
  });

  const handleCreateCategory = (e: React.FormEvent) => {
    e.preventDefault();
    if (newCategoryName.trim()) {
      createCategoryMutation.mutate(newCategoryName.trim());
    }
  };

  const handleDeleteCategory = (category: string) => {
    if (confirm(`Êtes-vous sûr de vouloir supprimer la catégorie "${category}" ? Cette action retirera la catégorie de tous les produits concernés.`)) {
      deleteCategoryMutation.mutate(category);
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center" data-testid="text-categories-management">
          <Tags className="h-5 w-5 mr-2" />
          Gestion des Catégories
        </CardTitle>
      </CardHeader>
      <CardContent>
        {isLoading ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
            <p className="mt-2 text-gray-600">Chargement des catégories...</p>
          </div>
        ) : (
          <div className="space-y-6">
            {/* Add new category form */}
            <div className="bg-gray-50 p-4 rounded-lg border">
              <h3 className="text-sm font-semibold text-gray-700 mb-3">Ajouter une nouvelle catégorie</h3>
              <form onSubmit={handleCreateCategory} className="flex gap-2">
                <Input
                  type="text"
                  placeholder="Nom de la catégorie..."
                  value={newCategoryName}
                  onChange={(e) => setNewCategoryName(e.target.value)}
                  disabled={createCategoryMutation.isPending}
                  className="flex-1"
                  data-testid="input-new-category"
                />
                <Button
                  type="submit"
                  disabled={!newCategoryName.trim() || createCategoryMutation.isPending}
                  className="flex items-center"
                  data-testid="button-add-category"
                >
                  <Plus className="h-4 w-4 mr-1" />
                  Ajouter
                </Button>
              </form>
            </div>

            {/* Existing categories */}
            <div className="space-y-4">
            {categories.length > 0 ? (
              <>
                <p className="text-sm text-gray-600 mb-4">
                  Voici les catégories actuellement utilisées dans vos produits. Vous pouvez les supprimer si nécessaire.
                </p>
                <div className="grid gap-3" data-testid="list-categories">
                  {categories.map((category) => (
                    <div
                      key={category}
                      className="flex items-center justify-between p-3 border rounded-lg bg-gray-50"
                      data-testid={`item-category-${category}`}
                    >
                      <div className="flex items-center space-x-3">
                        <Badge className="bg-primary text-white" data-testid={`badge-category-${category}`}>
                          {category}
                        </Badge>
                      </div>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleDeleteCategory(category)}
                        className="text-red-600 hover:text-red-800"
                        disabled={deleteCategoryMutation.isPending}
                        data-testid={`button-delete-category-${category}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  ))}
                </div>
              </>
            ) : (
              <div className="text-center py-12">
                <Tags className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-600" data-testid="text-no-categories">
                  Aucune catégorie trouvée
                </p>
                <p className="text-sm text-gray-500 mt-2">
                  Les catégories apparaîtront ici lorsque vous ajouterez des produits avec des catégories.
                </p>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/admin/delivery-pri.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Truck, MapPin } from "lucide-react";
import { Wilaya } from "@shared/schema";

export default function DeliveryPricing() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedWilayaId, setSelectedWilayaId] = useState("");
  const [newPrice, setNewPrice] = useState("");

  const { data: wilayas = [], isLoading } = useQuery<Wilaya[]>({
    queryKey: ["/api/wilayas"],
  });

  const updateDeliveryPriceMutation = useMutation({
    mutationFn: async ({ id, price }: { id: string; price: string }) => {
      const response = await apiRequest("PUT", `/api/admin/wilayas/${id}/delivery-price`, { price });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Prix mis à jour",
        description: "Le prix de livraison a été modifié avec succès",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/wilayas"] });
      setSelectedWilayaId("");
      setNewPrice("");
    },
    onError: (error) => {
      toast({
        title: "Erreur",
        description: "Impossible de mettre à jour le prix de livraison",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedWilayaId || !newPrice) {
      toast({
        title: "Erreur",
        description: "Veuillez sélectionner une wilaya et saisir un prix",
        variant: "destructive",
      });
      return;
    }

    const price = parseFloat(newPrice);
    if (isNaN(price) || price < 0) {
      toast({
        title: "Erreur",
        description: "Veuillez saisir un prix valide",
        variant: "destructive",
      });
      return;
    }

    updateDeliveryPriceMutation.mutate({ id: selectedWilayaId, price: newPrice });
  };

  const formatPrice = (price: string) => {
    const numPrice = parseFloat(price);
    return `${numPrice.toLocaleString()} DA`;
  };

  return (
    <div className="grid md:grid-cols-2 gap-6">
      {/* Update Form */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center" data-testid="text-delivery-form-title">
            <Truck className="h-5 w-5 mr-2" />
            Modifier les tarifs
          </CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4" data-testid="form-delivery-pricing">
            <div>
              <Label htmlFor="wilaya">Wilaya</Label>
              <Select
                value={selectedWilayaId}
                onValueChange={setSelectedWilayaId}
              >
                <SelectTrigger className="mt-1" data-testid="select-delivery-wilaya">
                  <SelectValue placeholder="Sélectionner une wilaya..." />
                </SelectTrigger>
                <SelectContent>
                  {wilayas.map((wilaya) => (
                    <SelectItem key={wilaya.id} value={wilaya.id}>
                      {wilaya.code} - {wilaya.name} ({formatPrice(wilaya.deliveryPrice)})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="price">Prix de livraison (DA)</Label>
              <Input
                id="price"
                type="number"
                step="0.01"
                min="0"
                value={newPrice}
                onChange={(e) => setNewPrice(e.target.value)}
                placeholder="0.00"
                className="mt-1"
                data-testid="input-delivery-price"
              />
            </div>

            <Button
              type="submit"
              className="w-full bg-primary hover:bg-primary-dark"
              disabled={updateDeliveryPriceMutation.isPending}
              data-testid="button-update-delivery-price"
            >
              {updateDeliveryPriceMutation.isPending ? "Mise à jour..." : "Mettre à jour"}
            </Button>
          </form>
        </CardContent>
      </Card>

      {/* Current Pricing List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center" data-testid="text-pricing-list-title">
            <MapPin className="h-5 w-5 mr-2" />
            Tarifs actuels
          </CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary mx-auto"></div>
              <p className="mt-2 text-sm text-gray-600">Chargement...</p>
            </div>
          ) : (
            <div className="space-y-2 max-h-80 overflow-y-auto" data-testid="list-delivery-prices">
              {wilayas.map((wilaya) => (
                <div
                  key={wilaya.id}
                  className="flex justify-between items-center py-2 border-b border-gray-100 last:border-b-0"
                  data-testid={`item-wilaya-${wilaya.id}`}
                >
                  <div>
                    <span className="font-medium" data-testid={`text-wilaya-name-${wilaya.id}`}>
                      {wilaya.code} - {wilaya.name}
                    </span>
                  </div>
                  <div className="font-bold text-primary" data-testid={`text-wilaya-price-${wilaya.id}`}>
                    {formatPrice(wilaya.deliveryPrice)}
                  </div>
                </div>
              ))}
            </div>
          )}

          {wilayas.length === 0 && !isLoading && (
            <div className="text-center py-8">
              <MapPin className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              <p className="text-gray-600" data-testid="text-no-wilayas">
                Aucune wilaya configurée
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/admin/orders-table.content:
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ShoppingCart, Phone, MapPin, Eye } from "lucide-react";
import { OrderWithDetails } from "@shared/schema";

export default function OrdersTable() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: orders = [], isLoading } = useQuery<OrderWithDetails[]>({
    queryKey: ["/api/admin/orders"],
  });

  const updateOrderStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      const response = await apiRequest("PUT", `/api/admin/orders/${id}/status`, { status });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Statut mis à jour",
        description: "Le statut de la commande a été modifié avec succès",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/orders"] });
    },
    onError: (error) => {
      toast({
        title: "Erreur",
        description: "Impossible de mettre à jour le statut",
        variant: "destructive",
      });
    },
  });

  const handleStatusChange = (orderId: string, newStatus: string) => {
    updateOrderStatusMutation.mutate({ id: orderId, status: newStatus });
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      pending: { label: "En attente", className: "bg-yellow-100 text-yellow-800" },
      confirmed: { label: "Confirmée", className: "bg-blue-100 text-blue-800" },
      shipped: { label: "Expédiée", className: "bg-purple-100 text-purple-800" },
      delivered: { label: "Livrée", className: "bg-green-100 text-green-800" },
      cancelled: { label: "Annulée", className: "bg-red-100 text-red-800" },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.pending;

    return (
      <Badge className={config.className}>
        {config.label}
      </Badge>
    );
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("fr-FR", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const formatPrice = (price: string) => {
    const numPrice = parseFloat(price);
    return `${numPrice.toLocaleString()} DA`;
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center" data-testid="text-orders-management">
          <ShoppingCart className="h-5 w-5 mr-2" />
          Gestion des Commandes
        </CardTitle>
      </CardHeader>
      <CardContent>
        {isLoading ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
            <p className="mt-2 text-gray-600">Chargement des commandes...</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <Table data-testid="table-orders">
              <TableHeader>
                <TableRow>
                  <TableHead>Commande</TableHead>
                  <TableHead>Client</TableHead>
                  <TableHead>Wilaya</TableHead>
                  <TableHead>Total</TableHead>
                  <TableHead>Statut</TableHead>
                  <TableHead>Date</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {orders.map((order) => (
                  <TableRow key={order.id} data-testid={`row-order-${order.id}`}>
                    <TableCell>
                      <div className="font-medium" data-testid={`text-order-id-${order.id}`}>
                        #{order.id.slice(-8)}
                      </div>
                      <div className="text-sm text-gray-500">
                        {order.items.length} article{order.items.length > 1 ? "s" : ""}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div>
                        <div className="font-medium" data-testid={`text-customer-name-${order.id}`}>
                          {order.customerName}
                        </div>
                        <div className="text-sm text-gray-500 flex items-center">
                          <Phone className="h-3 w-3 mr-1" />
                          {order.customerPhone}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center">
                        <MapPin className="h-3 w-3 mr-1 text-gray-400" />
                        <span data-testid={`text-wilaya-${order.id}`}>
                          {order.wilaya.code} - {order.wilaya.name}
                        </span>
                      </div>
                      <div className="text-xs text-gray-500">
                        Livraison: {formatPrice(order.deliveryPrice)}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="font-bold text-primary" data-testid={`text-total-${order.id}`}>
                        {formatPrice(order.total)}
                      </div>
                      <div className="text-xs text-gray-500">
                        Sous-total: {formatPrice(order.subtotal)}
                      </div>
                    </TableCell>
                    <TableCell data-testid={`badge-status-${order.id}`}>
                      {getStatusBadge(order.status || "pending")}
                    </TableCell>
                    <TableCell>
                      <div className="text-sm" data-testid={`text-date-${order.id}`}>
                        {formatDate(order.createdAt!)}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex space-x-2">
                        <Select
                          value={order.status || "pending"}
                          onValueChange={(status) => handleStatusChange(order.id, status)}
                        >
                          <SelectTrigger className="w-32" data-testid={`select-status-${order.id}`}>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="pending">En attente</SelectItem>
                            <SelectItem value="confirmed">Confirmée</SelectItem>
                            <SelectItem value="shipped">Expédiée</SelectItem>
                            <SelectItem value="delivered">Livrée</SelectItem>
                            <SelectItem value="cancelled">Annulée</SelectItem>
                          </SelectContent>
                        </Select>
                        <Button
                          variant="ghost"
                          size="sm"
                          title="Voir les détails"
                          data-testid={`button-view-${order.id}`}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        )}

        {orders.length === 0 && !isLoading && (
          <div className="text-center py-12">
            <ShoppingCart className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600" data-testid="text-no-orders">
              Aucune commande trouvée
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

